import React, { useState } from "react";
import vintage from "../assets/images/vintage-comic.png"

const Contact = () => {

  const [formState, setFormState] = useState({
    name: '',
    email: '',
    message: '',
  });

  const [status, setStatus] = useState("Submit");

  const handleChange = (event) => {
    const { name, value } = event.target;

    setFormState({
      ...formState,
      [name]: value,
    });
  }

  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("Sending...");
    const { name, email, message } = e.target.elements;
    let details = {
      name: name.value,
      email: email.value,
      message: message.value,
    };
    let response = await fetch("http://localhost:5000/contact", {
      method: "POST",
      headers: {
        "Content-Type": "application/json;charset=utf-8",
      },
      body: JSON.stringify(details),
    });
    setStatus("Submit");
    let result = await response.json();
    alert(result.status);
  };

  return (
    <section id="contact" className="py-3">
      <div className="card p-4">
        <img src={vintage} alt="Vintage comic-style woman on phone" className="card__image" />
        <div className="card-title">
          <span>CONTACT ME</span>
        </div>
        <form className="contact-form" onSubmit={handleSubmit} method="POST">
          <div class="input-container">
            <input 
              type="text" 
              aria-describedby="name"
              id="name" 
              name="name" 
              value={formState.name} 
              onChange={handleChange} 
              required="required"
            />
            <label for="name">Name</label>
            <div class="bar"></div>
          </div>
          <div class="input-container">
            <input 
              type="text" 
              aria-describedby="email"
              id="email" 
              name="email" 
              value={formState.email} 
              onChange={handleChange} 
              required="required"
            />
            <label for="email">Email</label>
            <div class="bar"></div>
          </div>
          <div class="input-container">
            <input 
              type="text"  
              aria-describedby="message"
              id="message" 
              name="message" 
              value={formState.message} 
              onChange={handleChange} 
              rows="4"
              required="required"
            />
            <label for="message">Message</label>
            <div class="bar"></div>
          </div>
          <div className="app-form-group buttons">
            <button 
              type="submit"
              className="btn">{status}</button>
          </div>
        </form>
      </div>
    </section>
  );
};

export default Contact;








